components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}

components/elevate/navigation-list:
{{#if children}}
<a class="{{#if name '==' 'On Sale'}}on-sale{{/if}} show navPages-action has-subMenu{{#if is_active}} activePage{{/if}}" href="{{url}}">
   {{name}} 
    <i class="icon navPages-action-moreIcon" aria-hidden="true">
        <span class="icon-multimedia theme-icon" data-icon="&#xe902;"></span>
    </i>
</a> 
